<template>
	<div
		ref="containerDOM"
		class="video__container"
		:data-width="state.width"
		:data-height="state.height"
		:class="{ loading: !state.loaded && show }">
		<!-- 视频主体 -->
		<div
			class="video__wrap"
			:class="{
				loading: !state.loaded && show,
				show: state.show && show,
				error: state.isError,
			}"
			:style="{ aspectRatio: aspectRatio }">
			<slot>
				<template v-if="mounted">
					<!-- s 不使用用缩略图 -->
					<template v-if="!useThumb">
						<!--s 正常视频元素 -->
						<video
							v-if="!state.isError"
							ref="videoDom"
							v-lazy.src="src"
							:controls="showControls"
							:loop="loop"
							:muted="muted"
							:autoplay="autoplay && !hoverPlay"
							controlslist="nofullscreen noremoteplayback"
							disablepictureinpicture
							:draggable="draggable" />
						<!--s 加载错误时的显示元素 -->
						<BaseImg v-else :src="errorImg" />
					</template>
					<!-- s 使用用缩略图 -->
					<template v-else>
						<BaseImg :src="thumb" />
					</template>
				</template>
			</slot>
			<div v-if="!state.isError" class="play-icon">
				<i-material-symbols-play-circle />
			</div>
		</div>
		<!-- 其他内容(插槽) -->
		<slot name="other"></slot>
	</div>
</template>

<script setup lang="ts">
	// 导入工具函数
	import { onMounted } from "vue";
	import {
		ref,
		reactive,
		computed,
		onUnmounted,
		defineProps,
		withDefaults,
		defineEmits,
		watch,
		nextTick,
		onActivated,
	} from "vue";
	import type { Directive } from "vue";
	import BaseImg from "./base-img.vue";
	import { useElementHover } from "@vueuse/core";

	// 加载错误时的图片
	const errorImg =
		"data:image/png;base64,";
	// 定义props
	const props = withDefaults(
		defineProps<{
			src: string;
			initWidth?: number;
			initHeight?: number;
			useThumb?: boolean;
			thumb?: string;
			viewportSelector?: string;
			viewRootMargin?: IntersectionObserverInit["rootMargin"];
			observerOnce?: boolean;
			manualControl?: boolean;
			draggable?: boolean; // 是否允许拖拽
			initShow?: boolean;
			show?: boolean; //是否显示
			muted?: boolean; //是否静音
			loop?: boolean; //是否循环播放
			autoplay?: boolean; //自动播放
			showControls?: boolean; //是否显示视频控制栏
			hoverPlay?: boolean; // 是否悬浮播放(与autoplay不兼容,如果为true会忽视autoplay)
			hoverAnewStart?: boolean; // 是否在悬浮时从头播放
		}>(),
		{
			src: "",
			initWidth: 0,
			initHeight: 0,
			useThumb: false,
			thumb: "",
			viewportSelector: "",
			viewRootMargin: "0%",
			observerOnce: true,
			manualControl: false,
			draggable: true, // 默认允许拖拽
			initShow: false,
			show: true,
			muted: false, // 默认不静音
			loop: false, // 默认不循环播放
			autoplay: false, // 默认不自动播放
			showControls: true, // 默认显示控制栏
			hoverPlay: false, // 默认不悬浮播放
			hoverAnewStart: false, // 默认悬浮时不从头播放
		}
	);

	//s 挂载标识符
	const mounted = ref(false);
	onMounted(() => {
		// console.log("视频组件挂载");
		nextTick(() => {
			mounted.value = true;
		});
	});

	onActivated(() => {
		nextTick(() => {
			mounted.value = true;
			// loadVideo(props.src);
		});
	});

	onUnmounted(() => {
		// console.log("视频组件卸载");
		mounted.value = false;
	});

	//s 组件容器DOM
	const containerDOM = ref<HTMLElement | null>(null);

	//j 视口容器DOM
	const viewportDom = computed<IntersectionObserverInit["root"]>(() => {
		if (props.viewportSelector.trim()) {
			return document.querySelector(props.viewportSelector);
		} else {
			return null;
		}
	});

	//w 监听传入的src变化,变化时立即重新加载
	watch(
		() => props.src,
		(newSrc) => {
			// console.log("src变化", newSrc, oldSrc);
			if (mounted.value) {
				loadVideo(newSrc);
			}
		}
	);

	//w 监听外部传入的是否显示变量(如果不显示的时候就暂停)
	watch(
		() => props.show,
		(show) => {
			if (!videoDom.value || props.hoverPlay) return;
			if (show) {
				videoDom.value.play();
			} else {
				videoDom.value.pause();
			}
		}
	);

	//s 组件是否被鼠标悬浮
	const isHover = useElementHover(containerDOM);

	//s 监听组件悬浮状态变化
	watch(
		() => isHover.value,
		(nowIsHover) => {
			// console.log("悬浮状态变化");
			if (!videoDom.value) return;
			if (nowIsHover) {
				if (props.hoverAnewStart) {
					videoDom.value.currentTime = 0;
					videoDom.value.play();
				} else {
					videoDom.value.play();
				}
			} else {
				videoDom.value.pause();
				if (props.hoverAnewStart) {
					videoDom.value.currentTime = 0;
				}
			}
		}
	);

	// 定义状态
	const state = reactive({
		errorImg: errorImg,
		width: props.initWidth,
		height: props.initHeight,
		isError: ref(false),
		loaded: ref(props.initShow),
		show: ref(props.initShow),
	});

	// 定义宽高比
	const aspectRatio = computed(() => {
		if (state.isError) {
			return props.initWidth && props.initHeight
				? props.initWidth / props.initHeight
				: 16 / 9;
		} else {
			return state.width && state.height ? state.width / state.height : 16 / 9;
		}
	});

	// 定义video标签的ref
	const videoDom = ref<HTMLVideoElement | null>(null);

	export type returnInfo = {
		meta: {
			valid: boolean;
			width: number;
			height: number;
			aspectRatio: number; // 宽高比
			[key: string]: any;
		};
		dom?: HTMLImageElement;
		load?: Function;
	};

	// 定义事件
	const emit = defineEmits<{
		(e: "loaded", info: returnInfo): void;
		(e: "error"): void;
	}>();

	//f 获取视频宽高
	function getVideoSize(
		url: string
	): Promise<{ width: number; height: number }> {
		return new Promise((resolve, reject) => {
			const video = document.createElement("video");
			video.onloadedmetadata = function () {
				// 获取视频宽度和高度
				const width = video.videoWidth;
				const height = video.videoHeight;
				// 释放资源
				URL.revokeObjectURL(video.src);
				resolve({ width, height });
			};
			video.onerror = function () {
				reject("无法加载视频");
			};

			video.src = url;
			video.load();
		});
	}

	//f 加载
	const loadVideo = async (src: string) => {
		//f 视频加载函数
		const handleLoad = () => {
			if (videoDom.value) {
				videoDom.value.src = src;
				nextTick(() => {
					videoDom.value!.style.display = "block";
				});
			}
			state.loaded = true;
			state.show = true;
			state.isError = false;
		};
		// 获取视频尺寸信息
		getVideoSize(src)
			.then(({ width, height }) => {
				// 记录宽高信息
				state.width = width;
				state.height = height;

				// 对剩余的属性进行类型标注
				let info: returnInfo = {
					meta: {
						valid: true,
						...state,
						aspectRatio: width / height, // 宽高比.
					},
				};

				// 判断是否需要用户手动加载
				if (!props.manualControl) {
					// 如果用户不需要手动加载就立即加载
					handleLoad();
					// 触发loaded事件
					emit("loaded", info);
					return;
				}

				// 返回dom和load函数
				info.load = handleLoad; // 如果需要手动加载就将该方法返回
				// 触发loaded事件,同时返回info对象
				emit("loaded", info);
			})
			.catch(() => {
				console.log("视频加载错误", src);
				state.isError = true;
				state.loaded = true;
				// 触发error事件
				emit("error");
			});
	};

	// 自定义指令
	const vLazy: Directive = {
		mounted: () => {
			// 将任务放入宏队列(防止有些时候交叉检测失败的bug)
			setTimeout(() => {
				let src: string = props.src; // 默认使用传入src
				const handleIntersection = async (
					entries: IntersectionObserverEntry[]
				) => {
					// console.log(entries[0].isIntersecting);
					if (entries[0].isIntersecting) {
						// 判断是否只监听一次
						if (props.observerOnce) {
							// 停止监听
							observer.disconnect();
						}
						// 判断是否已经被加载过了
						if (state.loaded) {
							// 如果已经被加载就让其显示
							state.show = true;
							return;
						}

						// 执行加载函数
						loadVideo(src);
					} else {
						state.show = false; // 标记为不可见
					}
				};

				// 创建 IntersectionObserver
				const options: IntersectionObserverInit = {
					root: viewportDom.value,
					rootMargin: props.viewRootMargin,
				};
				// console.log(viewportDom.value);
				const observer = new IntersectionObserver(handleIntersection, options);
				// 开始监听
				if (containerDOM.value) {
					observer.observe(containerDOM.value);
				} else {
					console.log("监听失效,可能未找到containerDOM");
				}
			});
		},
	};
</script>

<style lang="scss" scoped>
	.video__container {
		position: relative;
		box-sizing: border-box; // 盒子模型，确保边框不会影响内容的大小。
		// background: rgba(0, 0, 0, 0.5);
		background: transparent;
		* {
			box-sizing: border-box;
		}
	}
	.video__wrap {
		opacity: 0; //默认不显示
		transition: 0.5s ease-out; // 添加过渡效果
	}
	// 加载中的样式
	.video__wrap.loading {
		opacity: 0;
	}
	// 加载完成且可见的样式
	.video__wrap.show {
		opacity: 1;
	}
	// 加载错误的样式
	.video__wrap.error {
		transform: scale(0.8);
		opacity: 0.5;
		object-fit: contain;
	}

	video {
		display: block;
		width: 100%;
		height: auto;
		padding: 0;
		object-fit: cover;
		background: transparent;
		/* 禁止选中文字 */
		user-select: none;
		/* 禁止图文拖拽 */
		-webkit-user-drag: none;
		transition: 0.5s ease-out; // 添加过渡效果
	}

	/* 加载动画 */
	.video__container.loading::before {
		content: "";
		position: absolute;
		top: 50%;
		left: 50%;
		transform: translate(-50%, -50%);
		width: 30%;
		// height: 50%;
		aspect-ratio: 1;
		max-width: 80%;
		max-height: 80%;
		border: 5px solid #ccc;
		border-radius: 50%;
		border-top-color: #007bff;
		animation: spin 1s linear infinite; /* 旋转动画 */
		z-index: 100;
	}
	/* 加载动画定义 */
	@keyframes spin {
		0% {
			transform: translate(-50%, -50%) rotate(0deg);
		}
		100% {
			transform: translate(-50%, -50%) rotate(360deg);
		}
	}
	//s 播放按钮
	.play-icon {
		position: absolute;
		display: flex;
		pointer-events: none;
		inset: 0;
		opacity: 0.8;
		background-repeat: no-repeat;

		transition: 0.5s ease;
		& > svg {
			width: 100%;
			height: 100%;
			aspect-ratio: 1;
			color: palevioletred;
			transform: scale(0.5);
			backdrop-filter: blur(10px);

			filter: drop-shadow(0 0 10px rgba(255, 255, 0, 0.5));
		}
	}

	.video__container:hover .play-icon {
		opacity: 0;
	}
</style>
